        return {trslt:{x:trs.trslt.x,y:trs.trslt.y},rotate:{cx:trs.rotate.cx,cy:trs.rotate.cy,angle:trs.rotate.angle},scale:{x:trs.scale.x,y:trs.scale.y}};
